package Tree;import java.util.List;import java.util.LinkedList;public class JUMP extends Stm {    public Exp1 exp;    public LinkedList<Temp.Label> targets;    public JUMP(Exp1 e, LinkedList<Temp.Label> t) { exp=e; targets=t; }    public JUMP(Temp.Label target) {		exp = new NAME(target);		targets = new LinkedList<Temp.Label>();		targets.addFirst(target);    }    public LinkedList<Exp1> kids() {		LinkedList<Exp1> kids = new LinkedList<Exp1>();		kids.addFirst(exp);		return kids;    }    public Stm build(LinkedList<Exp1> kids) {    	return new JUMP(kids.getFirst(), targets);    }    public void accept(IntVisitor v, int d) { v.visit(this, d); }    public void accept(CodeVisitor v) { v.visit(this); }    public <R> R accept(ResultVisitor<R> v) { return v.visit(this); }}